mapper.select:
select u.admin_id, u.user_name, u.nick_name, u.avatar, u.phonenumber, u.sex, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark
from sys_admin u
where u.del_flag = '0'

    AND u.admin_id = #{adminId}


    AND u.user_name like concat('%', #{userName}, '%')


    AND u.status = #{status}


    AND u.phonenumber like concat('%', #{phonenumber}, '%')


    AND date_format(u.create_time,'%Y%m%d') >= date_format(#{params.beginTime},'%Y%m%d')


    AND date_format(u.create_time,'%Y%m%d') <= date_format(#{params.endTime},'%Y%m%d')


${params.dataScope}

select distinct u.admin_id, u.user_name, u.nick_name, u.avatar, u.phonenumber, u.status, u.create_time from sys_admin u left join sys_admin_role ur on u.admin_id = ur.admin_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
${params.dataScope}

mapper.update:
update sys_admin
nick_name = #{nickName}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where admin_id = #{adminId}
